mapper.select:
SELECT a.material_id, coalesce(omr_notifi_no, sorc_no) as sorc_no, a.model_id, (select name from model where model_id = a.model_id) as model_name, a.serial_no, a.ocm, a.section_id, (select name from section where section_id = a.section_id) as section_name, (select p1.process_code from position p1 join production_feature p2 on p1.position_id = p2.position_id where p2.material_id = a.material_id and p1.line_id <>13 and p2.operate_result not in (5, 8) order by (1-ISNULL(p2.action_time)), p2.action_time desc limit 1) as processing_position, a.reception_time, a.agreed_date from Material a where a.material_id in
#{id}
and break_back_flg = 0;

SELECT `m`.material_id, `m`.`fix_type`, coalesce(omr_notifi_no, sorc_no) as sorc_no, `m`.model_id, mdl.name as model_name, (select date_format(finish_time, '%Y-%m-%d') from production_feature where position_id in (select position_id from position where special_page = 'quotation') and position_id != 101 and production_feature.material_id in
#{id}
and material_id = `m`.material_id order by finish_time desc limit 1) as finish_time, `m`.serial_no, `m`.ocm, `m`.section_id, `m`.level, `m`.service_repair_flg, `m`.direct_flg + (`m`.direct_flg * `m`.Scheduled_expedited) as direct_flg, `m`.selectable, `m`.reception_time, `m`.agreed_date, mdl.kind, mdl.with_case as unrepair_flg, mdl.series as processing_position, exists (select 1 from material_tag where material_id = m.material_id and tag_type = 6) as contract_related, (CASE WHEN `m`.level = 9 OR `m`.level = 91 OR `m`.level = 92 OR `m`.level = 93 OR `m`.level = 96 OR `m`.level = 97 OR `m`.level = 98 OR `m`.level = 99 THEN 7 WHEN mdl.category_id = 00000000013 OR `m`.fix_type = 2 THEN 4 WHEN mdl.kind = 06 THEN 5 WHEN mdl.kind = 03 THEN 6 WHEN mdl.kind = 07 THEN 5 WHEN `m`.level = 1 THEN 8 ELSE 14 END + (coalesce(`m`.service_repair_flg, 0) = 2)) as ticket_flg, coalesce(`m`.scheduled_date, f_addworkdays(`m`.agreed_date, CASE WHEN `m`.level = 9 OR `m`.level = 91 OR `m`.level = 92 OR `m`.level = 93 OR `m`.level = 96 OR `m`.level = 97 OR `m`.level = 98 OR `m`.level = 99 THEN 2 ELSE 5 END )) as scheduled_date
from Material m join v_model mdl on `m`.model_id = mdl.model_id where `m`.material_id in
#{id}

mapper.update:
UPDATE Material SET scheduled_expedited = 1 where material_id in
#{id}
and scheduled_expedited < 1


