mapper.select:
SELECT distinct a.section_id, c.name as section_name, a.position_id, e.action_date as action_time, b.process_code, b.name as position_name, d.name as line_name, e.operator_name as operator_name, processing.processing_count, waiting.waiting_count, e.processed_count as processed_count FROM position_of_section a join position b on a.position_id = b.position_id join section c on a.section_id = c.section_id join line d on b.line_id = d.line_id left join ( select section_id, position_id, count(material_id) as processing_count from production_feature where operate_result = 1 group by section_id, position_id ) processing ON a.section_id = processing.section_id AND a.position_id = processing.position_id left join ( select section_id, position_id, count(material_id) as waiting_count from production_feature where finish_time is null and operate_result not in (1, 5) group by section_id, position_id ) waiting ON a.section_id = waiting.section_id AND a.position_id = waiting.position_id left join ( select group_concat(distinct o.name separator ' ') as operator_name, pf.position_id, pf.section_id, date(action_time) as action_date, count(material_id) as processed_count from production_feature pf join operator o on o.operator_id = pf.operator_id where operate_result = 2
and pf.action_time >= #{action_time_start}
and date(pf.action_time) <= #{action_time_end}
and pf.operator_id = #{operator_id}
and pf.section_id = #{section_id}
and pf.position_id = #{position_id}
group by pf.position_id, pf.section_id, action_date ) e on a.position_id = e.position_id and a.section_id = e.section_id
e.action_date is not null
and e.action_date >= #{action_time_start}
and date(e.action_date) <= #{action_time_end}
and b.process_code = #{process_code}
and a.section_id = #{section_id}
and a.position_id = #{position_id}
and d.line_id = #{line_id}
-- order by (select count(name) from operator o join production_feature p on o.operator_id = p.operator_id where p.position_id = a.position_id and p.section_id = a.section_id and operate_result not in (5, 8) and date_format(p.action_time, '%Y-%m-%d') = date_format(e.action_time, '%Y-%m-%d')) desc

SELECT a.action_time, a.finish_time, coalesce(b.omr_notifi_no, b.sorc_no) as sorc_no, (select name from model where model_id = b.model_id) as model_name, a.operator_id, (select name from operator where operator_id = a.operator_id) as operator_name, a.operate_result FROM production_feature a, material b WHERE a.material_id = b.material_id and a.operate_result not in (5, 8) and a.position_id = #{position_id} and a.section_id = #{section_id} and a.action_time between #{action_time} and DATE_ADD(#{action_time}, INTERVAL 1 DAY)
and a.operator_id = #{operator_id}
order by a.action_time asc
