mapper.select:
SELECT ma.material_id, coalesce(ma.omr_notifi_no, ma.sorc_no) as sorc_no, ma.serial_no, ma.reception_time, ma.agreed_date, mo.name as model_name, pr.finish_time, pr.operate_result, op.name as operation_name, ma.fix_type, ma.service_repair_flg, ma.direct_flg, p.process_code FROM material ma LEFT JOIN model mo ON ma.model_id=mo.model_id JOIN production_feature pr ON pr.material_id=ma.material_id and pr.operate_result in (2,6) JOIN position p ON pr.position_id = p.position_id JOIN production_feature prEx ON prEx.material_id=ma.material_id AND prEx.pcs_inputs like '%"EN61___":"%' AND pr.rework = prEx.rework JOIN operator op ON prEx.operator_id=op.operator_id
pr.position_id in (select position_id from position where line_id='15') and ma.break_back_flg = 0
and mo.category_id=#{category_id}
and ma.model_id=#{model_id}
and ma.serial_no=#{serial_no}
and (ma.sorc_no LIKE CONCAT('%',#{sorc_no},'%') or ma.omr_notifi_no LIKE CONCAT('%',#{sorc_no},'%'))
and ma.section_id=#{section_id}
and ma.reception_time>=#{reception_time_start}
and date(ma.reception_time)<=#{reception_time_end}
and pr.finish_time>=#{complete_date_start}
and date(pr.finish_time)<=#{complete_date_end}
and ma.scheduled_date>=#{scheduled_date_start}
and date(ma.scheduled_date)<=#{scheduled_date_end}
and op.operator_id=#{operator_id}
and ma.fix_type=#{fix_type}
and ma.service_repair_flg=#{service_repair_flg}
and ma.direct_flg=#{direct_flg}
ORDER BY pr.finish_time DESC;
