mapper.select:
SELECT cur.check_unqualified_record_key, cur.manage_id, cur.object_type, cur.happen_time, COALESCE(dm.manage_code,tm.manage_code,'') AS manage_code, COALESCE(dt.name,tm.tool_name,'') AS name, dm.devices_type_id, tm.tools_type_id, cur.check_item, cur.unqualified_status, cur.product_content, cur.position_handle, cur.object_handle, cur.repair_date_start, cur.repair_date_end, responsible_op.name AS responsible_operator_name, line_op.name AS line_leader_name, cur.object_final_handle_result, cur.product_result, tm.tools_no FROM check_unqualified_record cur LEFT JOIN devices_manage dm ON cur.manage_id=dm.devices_manage_id AND cur.object_type=1 LEFT JOIN devices_type dt ON dt.devices_type_id=dm.devices_type_id LEFT JOIN tools_manage tm ON cur.manage_id=tm.tools_manage_id AND cur.object_type=2 LEFT JOIN operator responsible_op ON cur.responsible_operator_id=responsible_op.operator_id LEFT JOIN operator line_op ON cur.line_leader_id=line_op.operator_id
dm.manage_code LIKE CONCAT('%',#{manage_code},'%') OR tm.manage_code LIKE CONCAT('%',#{manage_code},'%')
AND dt.name LIKE CONCAT('%',#{name},'%') OR tm.tool_name LIKE CONCAT('%',#{name},'%')
AND cur.check_item LIKE CONCAT('%',#{check_item},'%')
AND cur.product_content=#{product_content}
AND cur.position_handle=#{position_handle}
AND cur.object_handle=#{object_handle}
AND cur.object_final_handle_result=#{object_final_handle_result}
AND cur.happen_time >= #{happen_time_start}
AND DATE(cur.happen_time) <= #{happen_time_end}
AND cur.repair_date_start >= #{repair_date_start_start}
AND cur.repair_date_start <= #{repair_date_start_end}
order by (line_leader_handle_time is not null), (technology_handle_time is not null), happen_time desc

mapper.update:
UPDATE check_unqualified_record SET position_handle=#{position_handle}, line_leader_id=#{line_leader_id}, line_leader_handle_time=CURRENT_TIMESTAMP()
, borrow_object_id=#{borrow_object_id}
check_unqualified_record_key=#{check_unqualified_record_key}


UPDATE 
	check_unqualified_record
SET
	manager_id=#{manager_id},
	manager_handle_time=CURRENT_TIMESTAMP(),
	product_result=#{product_result}



	,
	object_handle=#{object_handle}




	,
	product_content=#{product_content}




	check_unqualified_record_key=#{check_unqualified_record_key}

mapper.select:
select section.name as section_name, position.process_code from
devices_manage manage left join section on manage.section_id=section.section_id left join position on manage.position_id=position.position_id where manage.devices_manage_id=#{borrow_object_id}
tools_manage manage left join section on manage.section_id=section.section_id left join position on manage.position_id=position.position_id where manage.tools_manage_id=#{borrow_object_id}
