mapper.select:
SELECT manage_code, devices_manage_id as manage_id FROM devices_manage dm JOIN devices_type dt ON dm.devices_type_id = dt.devices_type_id
status IN (1 , 4) AND dt.specialized = 2
and devices_manage_id <> #{manage_id}

SELECT dm.manage_code, td.manage_id, td.seq, td.regular_torque, td.deviation, td.usage_point, td.hp_scale, (CASE hp_scale WHEN '1' then round((ceil((regular_torque - deviation)*100))/100, 2) WHEN '2' then round((ceil((regular_torque - deviation)*1000))/1000, 3) ELSE '0' END) AS regular_torque_lower_limit, (CASE hp_scale WHEN '1' then round((floor((regular_torque + deviation)*100))/100, 2) WHEN '2' then round((floor((regular_torque + deviation)*1000))/1000, 3) ELSE '0' END) AS regular_torque_upper_limit FROM torsion_device td LEFT JOIN devices_manage dm ON dm.devices_manage_id = td.manage_id
dm.delete_flg = 0
AND td.manage_id =#{manage_id}
AND dm.manage_code LIKE CONCAT('%',#{manage_code},'%')
AND td.seq=#{seq}
AND td.usage_point LIKE CONCAT('%',#{usage_point},'%')
AND td.hp_scale=#{hp_scale}
AND dm.section_id=#{section_id}
AND dm.position_id=#{position_id} AND dm.status = 1 AND dm.manage_level <= 2
ORDER BY dm.manage_code, td.seq

SELECT dm.devices_manage_id,dt.name, dm.section_id, dm.position_id, pos.process_code FROM torsion_device td join devices_manage dm on td.manage_id = dm.devices_manage_id join devices_type dt on dm.devices_type_id = dt.devices_type_id left join section sec on dm.section_id = sec.section_id join position pos on dm.position_id = pos.position_id where dm.delete_flg = 0 and dm.status = 1 and manage_level <= 2 and dm.section_id = #{section_id}
and 'QR-B31002-17 力矩螺丝刀点检表' like concat('%', #{sheet_manage_no}, '%')
and dm.manage_code like concat('%', #{manage_code}, '%')
and dm.model_name like concat('%', #{model_name}, '%')
and dm.position_id = #{position_id}
and NOT EXISTS (SELECT 1 FROM check_unqualified_record cur where cur.manage_id = dm.devices_manage_id and cur.manage_id = 1 and repair_date_start <= current_date and (repair_date_end IS null or repair_date_end >= current_date)) group by dm.section_id, dm.position_id ;

SELECT dm.devices_manage_id,dt.name, dm.section_id, dm.position_id, concat(sec.name, '\n', pos.process_code) as process_code FROM torsion_device td join devices_manage dm on td.manage_id = dm.devices_manage_id join devices_type dt on dm.devices_type_id = dt.devices_type_id left join section sec on dm.section_id = sec.section_id join position pos on dm.position_id = pos.position_id where dm.delete_flg = 0 and dm.status = 1 and manage_level <= 2
and manager_operator_id = #{operator_id}
and 'QR-B31002-17 力矩螺丝刀点检表' like concat('%', #{sheet_manage_no}, '%')
and dm.manage_code like concat('%', #{manage_code}, '%')
and dm.model_name like concat('%', #{model_name}, '%')
and dm.devices_type_id = #{devices_type_id}
and dm.position_id = #{position_id}
group by dm.section_id, dm.position_id ;
