mapper.select:
SELECT section.name AS section_name, position.process_code, CASE WHEN cm.origin_material_id IS NOT NULL THEN '组件' WHEN so.manage_serial_no IS NOT NULL THEN concat('(源)', ma.omr_notifi_no) ELSE ma.omr_notifi_no END as omr_notifi_no, CASE WHEN cm.origin_material_id IS NOT NULL THEN cm.serial_no WHEN so.manage_serial_no IS NOT NULL THEN concat('序列:', so.manage_serial_no) ELSE ma.serial_no END as serial_no, model.name AS model_name, dj.content, dj.drying_time, dm.manage_code, dp.slot, dp.start_time, dp.end_time, position.position_id, dj.drying_job_id, ma.material_id FROM drying_process dp JOIN material ma ON ma.material_id = dp.material_id JOIN model ON model.model_id = ma.model_id JOIN drying_job dj ON dj.drying_job_id = dp.drying_job_id LEFT JOIN devices_manage dm ON dm.devices_manage_id = dj.device_manage_id JOIN section ON section.section_id = ma.section_id JOIN position ON position.position_id = dj.position_id LEFT JOIN snout_origin so ON so.material_id = ma.material_id AND position.position_id = 24 LEFT JOIN component_manage cm ON cm.origin_material_id = ma.material_id AND position.line_id = 13
model.model_id = #{model_id}
AND ma.omr_notifi_no LIKE CONCAT('%',#{omr_notifi_no},'%')
AND dj.position_id = #{position_id}
AND ma.section_id = #{section_id}
AND dj.device_manage_id = #{device_manage_id}
AND dp.end_time IS NULL
AND dp.end_time IS NOT NULL
AND ma.material_id = #{material_id}
ORDER BY dp.start_time DESC

select dod.device_manage_id, dod.setting_temperature, dod.slot, dm.manage_code, dm.model_name, pos.process_code, sec.name as section_name, usdm.using_slots from drying_oven_device dod join devices_manage dm on dod.device_manage_id = dm.devices_manage_id join section sec on dm.section_id = sec.section_id join position pos on dm.position_id = pos.position_id left join ( select device_manage_id, group_concat(slot) as using_slots from drying_process where end_time is null group by device_manage_id ) usdm on dod.device_manage_id = usdm.device_manage_id
WHERE dod.device_manage_id = #{device_manage_id}
WHERE dod.setting_temperature = ( select setting_temperature from drying_oven_device where device_manage_id = #{device_manage_id} )

SELECT material_id, drying_job_id, start_time
FROM `drying_process`
WHERE material_id = #{material_id}
AND `end_time` IS NULL
AND `drying_job_id` in (
	SELECT drying_job_id FROM drying_job 
	WHERE position_id = #{position_id}
);

mapper.update:
UPDATE `drying_process`
SET
`end_time` = current_timestamp
WHERE material_id = #{material_id}
AND `start_time` = #{start_time}
AND `drying_job_id` = #{drying_job_id}
;

mapper.select:
SELECT dp.start_time, dm.manage_code, slot, dj.content, dj.hardening_condition, dj.drying_time FROM `drying_process` dp JOIN `drying_job` dj ON dp.drying_job_id = dj.drying_job_id LEFT JOIN `devices_manage` dm ON dp.device_manage_id = dm.devices_manage_id WHERE material_id = #{material_id} AND `end_time` IS NULL
LIMIT 1
AND dj.position_id = #{position_id}
;
