mapper.select:
SELECT OPERATOR_ID, o.NAME, JOB_NO, WORK_COUNT_FLG, SECTION_NAME, LINE_NAME, ROLE_NAME, RANK_KIND, EMAIL, PX, o.UPDATED_BY, o.UPDATED_TIME FROM V_OPERATOR_NAMED o
DELETE_FLG = 0
AND OPERATOR_ID = #{operator_id}
AND o.NAME LIKE CONCAT('%', #{name}, '%')
AND JOB_NO = #{job_no}
AND WORK_COUNT_FLG = #{work_count_flg}
AND SECTION_ID = #{section_id}
AND LINE_ID = #{line_id}
AND o.ROLE_ID = #{role_id}
AND RANK_KIND = #{rank_kind}
AND EXISTS (SELECT * FROM operator_abilities a WHERE o.OPERATOR_ID = a.OPERATOR_ID and a.POSITION_ID = #{position_id})
AND EXISTS (SELECT * FROM operator_af_abilities aa WHERE o.OPERATOR_ID = aa.OPERATOR_ID and aa.production_type = #{af_ability})
AND OPERATOR_ID <> 1 ORDER BY UPDATED_TIME DESC;

mapper.update:
UPDATE OPERATOR SET NAME = #{name}, JOB_NO = #{job_no},
PWD = #{pwd}, PWD_DATE = #{pwd_date},
WORK_COUNT_FLG = #{work_count_flg}, SECTION_ID = #{section_id}, LINE_ID = #{line_id}, ROLE_ID = #{role_id}, POSITION_ID = #{position_id}, EMAIL = #{email}, PX = #{px}, UPDATED_BY = #{updated_by}, UPDATED_TIME = current_timestamp WHERE OPERATOR_ID = #{operator_id};

 
UPDATE OPERATOR SET
	PWD = #{pwd},
	PWD_DATE = #{pwd_date}
WHERE OPERATOR_ID = #{operator_id};

mapper.select:
 
SELECT ROLE_ID
FROM ROLE_OF_OPERATOR
WHERE OPERATOR_ID = #{operator_id}
AND EXPIRATION > current_timestamp;

 
SELECT `ro`.ROLE_ID as role_id, `r`.`NAME`
FROM ROLE_OF_OPERATOR `ro`
JOIN ROLE `r`
ON `ro`.ROLE_ID = `r`.ROLE_ID
WHERE `ro`.OPERATOR_ID = #{operator_id}
AND `ro`.EXPIRATION > current_timestamp;

mapper.update:
 
UPDATE ROLE_OF_OPERATOR SET EXPIRATION = #{expiration},
WHERE OPERATOR_ID = #{operator_id}
AND ROLE_ID = #{role_id};

mapper.select:
 
SELECT POSITION_ID
FROM OPERATOR_ABILITIES
WHERE OPERATOR_ID = #{operator_id}


	AND CHIEF = #{chief}


SELECT * FROM v_operator_named vo
WHERE
	delete_flg = 0
AND (vo.role_id = #{role_id}
OR EXISTS(
	SELECT 1 FROM role_of_operator
	WHERE role_id = #{role_id}
	AND operator_id = vo.operator_id
));

		
		SELECT * FROM v_operator_named vo
		WHERE
 			delete_flg = 0
		AND EXISTS(
			SELECT 1 FROM operator_abilities
			WHERE position_id = #{position_id}
			AND operator_id = vo.operator_id
		);


SELECT production_type FROM `operator_af_abilities`
WHERE OPERATOR_ID = #{operator_id}
