mapper.select:

SELECT
	count(optional_fix_id)
FROM optional_fix
WHERE standard_code = #{standard_code}
  and delete_flg = 0


	AND optional_fix_id != #{optional_fix_id}

SELECT opf.optional_fix_id, opf.standard_code, opf.infection_item FROM optional_fix_for_rank opfr JOIN optional_fix opf ON opf.optional_fix_id = opfr.optional_fix_id
WHERE opfr.rank = #{rank}
group by opf.optional_fix_id order by opf.standard_code asc
