mapper.select:
select pbs.partial_id, mo.name as model_name, pbs.level, pa.code, pa.name as partial_name, pbs.quantity, mls.echelon FROM partial_bom_set pbs LEFT JOIN partial pa ON pbs.partial_id = pa.partial_id LEFT JOIN model_level_set mls ON pbs.model_id = mls.model_id AND pbs.level=mls.level LEFT JOIN model mo ON mo.model_id=pbs.model_id
pbs.model_id =#{model_id}
AND pbs.level=#{level}
AND pa.code LIKE CONCAT("%",#{code},"%")
order by mls.level,mo.name

mapper.update:

UPDATE 
	partial_bom_set
SET 
	partial_id=#{new_partial_id}
WHERE
	model_id=#{model_id} AND partial_id=#{partial_id}
