mapper.select:
SELECT POSITION_ID, NAME, LINE_ID, LINE_NAME, PROCESS_CODE, UPDATED_BY, UPDATED_TIME FROM V_POSITION
DELETE_FLG in (0, 2)
AND POSITION_ID = #{position_id}
AND LINE_ID = #{line_id}
AND NAME LIKE CONCAT('%', #{name}, '%')
AND PROCESS_CODE = #{process_code}
ORDER BY PROCESS_CODE, UPDATED_TIME DESC;

mapper.update:
 
UPDATE POSITION 
	SET mapping_position_id = null
WHERE mapping_position_id = #{mapping_position_id};

 
UPDATE POSITION 
	SET mapping_position_id = #{mapping_position_id}
WHERE POSITION_ID = #{position_id};

 
UPDATE POSITION 
	SET delete_flg = 0
WHERE PROCESS_CODE = #{process_code}
AND delete_flg = 2;
