mapper.select:
select ot.name as responsible_operator, l.name as line_name, s.name as section_name, p.process_code, tm.tool_name as tools_name, o.name as manager_name, tm.tools_manage_id, tm.manage_code, tm.tools_no, tm.tools_type_id, tm.total_price, tm.classify, tm.manage_level, tm.manager_operator_id, tm.section_id, tm.line_id, tm.position_id, tm.location, tm.import_date, tm.provide_date, tm.waste_date, tm.delete_flg, op.name as updated_by, tm.updated_time, tm.status, tm.responsible_operator_id, tm.comment, tm.order_date from tools_manage tm left join tools_type tt on tt.tools_type_id =tm.tools_type_id left join OPERATOR O ON tm.manager_operator_id = O.OPERATOR_ID left join OPERATOR op ON tm.updated_by = op.OPERATOR_ID left join operator ot on tm.responsible_operator_id =ot.OPERATOR_ID left join position p on tm.position_id = p.position_id left join section s on tm.section_id = s.section_id left join line l on tm.line_id = l.line_id
tm.delete_flg=0
and tm.status in(${status})
and tm.manage_code LIKE CONCAT('%',#{manage_code},'%')
and tm.tools_no LIKE CONCAT('%',#{tools_no},'%')
and tm.tool_name LIKE CONCAT('%',#{tools_name},'%')
and tm.tools_type_id =#{tools_type_id}
and tm.section_id =#{section_id}
and tm.line_id =#{line_id}
and tm.manage_level = #{manage_level}
and tm.manager_operator_id = #{manager_operator_id}
and tm.position_id =#{position_id}
and tm.responsible_operator_id =#{responsible_operator_id}
and tm.order_date>=#{order_date_start}
and tm.order_date<=#{order_date_end}
and tm.import_date>=#{import_date_start}
and tm.import_date<=#{import_date_end}
and tm.waste_date>=#{waste_date_start}
and tm.waste_date<=#{waste_date_end}
ORDER BY status,updated_time DESC


  SELECT manage_code FROM tools_manage
  WHERE manage_code= #{manage_code} and delete_flg = 0



	and tools_manage_id <> #{tools_manage_id}
