mapper.select:
SELECT pa.code AS partial_code, pa.name AS partial_name, pph.identification, pph.foreboard_count, pph.start_date, pph.end_date, DATE(pph.updated_time) AS updated_time, op.name AS update_name, COALESCE(partial_order.order_num,0) AS order_num, COALESCE(bb.bo_num,0) AS bo_num FROM partial_prepair_history pph LEFT JOIN partial pa ON pph.partial_id = pa.partial_id LEFT JOIN operator op ON op.operator_id = pph.updated_by LEFT JOIN (SELECT mpd.partial_id AS partial_id, mp.order_date AS order_date, COUNT(1) AS order_num FROM material_partial_detail mpd JOIN material_partial mp ON mpd.material_id = mp.material_id AND mpd.occur_times = mp.occur_times GROUP BY partial_id ) AS partial_order ON partial_order.partial_id=pph.partial_id AND (partial_order.order_date >= pph.start_date AND partial_order.order_date < pph.end_date) LEFT JOIN (SELECT mpd.partial_id AS partial_id, mp.order_date AS order_date, SUM(bo_flg) AS bo_num FROM material_partial_detail mpd JOIN material_partial mp ON mpd.material_id = mp.material_id AND mpd.occur_times = mp.occur_times WHERE mpd.status IN(3,4) GROUP BY partial_id ) AS bb ON bb.partial_id=pph.partial_id AND (bb.order_date >= pph.start_date AND bb.order_date < pph.end_date)
1=1
AND pa.code LIKE CONCAT('%',#{partial_code},'%')
AND pa.name LIKE CONCAT('%',#{partial_name},'%')
AND pph.updated_by = #{updated_by}
AND pph.identification = #{identification}
AND pph.start_date>= #{start_date_start}
AND pph.start_date <= #{start_date_end}
AND pph.updated_time >= #{update_time_start}
AND DATE(pph.updated_time) <= #{update_time_end}
order by updated_time desc
