mapper.select:
SELECT pw.`key`, pw.warehouse_date, pw.dn_no, pw.step, SUM(dtl.quantity) AS quantity, (SELECT SUM(quantity) FROM fact_partial_warehouse LEFT JOIN af_production_feature ON fact_partial_warehouse.af_pf_key = af_production_feature.af_pf_key WHERE af_production_feature.production_type = 213 AND fact_partial_warehouse.partial_warehouse_key = pw.key ) AS collation_quantity FROM partial_warehouse pw JOIN partial_warehouse_detail dtl ON pw.key = dtl.key
AND pw.dn_no = #{dn_no}
AND pw.warehouse_date >= #{warehouse_date_start}
AND pw.warehouse_date <= #{warehouse_date_end}
AND pw.step = #{step}
AND pw.step = #{step}
AND (SELECT MAX(finish_time) FROM af_production_feature LEFT JOIN fact_partial_warehouse ON af_production_feature.af_pf_key = fact_partial_warehouse.af_pf_key WHERE fact_partial_warehouse.partial_warehouse_key = pw.key and af_production_feature.production_type = 213) BETWEEN
#{finish_date_start}
'2000-1-1'
AND
DATE_ADD(#{finish_date_end},INTERVAL 1 DAY)
'2100-1-1'
GROUP BY pw.key
