mapper.select:
select * from (select mp.order_date, cast(m.material_id as char) as material_id, mpd.material_partial_detail_key, mpd.quantity, mpd.price, mpd.belongs, (mpd.price * mpd.quantity) as loss_price, m.ocm_shipping_date, coalesce(m.omr_notifi_no, m.sorc_no) as sorc_no, m.serial_no, m.ocm, m.ocm_rank, m.level, mdl.KIND, mdl.name as model_name, case when mdl.kind = 03 then '否' when m.ocm_rank = 0 then '-' when m.ocm_rank = m.level mod 5 then '否' else '是' END change_rank, p.code, ld.service_free_flg, ld.comment, ld.liability_flg, ld.nogood_description from material_partial_detail mpd left join loss_detail ld ON ld.material_partial_detail_key = mpd.material_partial_detail_key left join material_partial mp ON mpd.material_id = mp.material_id and mpd.occur_times = mp.occur_times join material m ON m.material_id = mpd.material_id JOIN v_model mdl ON m.model_id = mdl.model_id JOIN partial p ON mpd.partial_id = p.partial_id
belongs>=3
and m.ocm_shipping_date =#{ocm_shipping_date}
and (m.ocm_shipping_date>=date_add(date_add(last_day(#{ocm_shipping_month}),interval 1 day),interval -1 month)) and (m.ocm_shipping_date <= last_day(#{ocm_shipping_month}))
and (m.sorc_no LIKE CONCAT('%',#{sorc_no},'%') or m.omr_notifi_no LIKE CONCAT('%',#{sorc_no},'%'))
and mp.order_date=#{order_date}
) mtl left join (select * from service_repair_manage where material_id like '0%') srm ON mtl.material_id = srm.material_id where (srm.service_repair_flg not in (1,2) or srm.service_repair_flg is null or (srm.service_repair_flg = 1 and srm.service_free_flg = 1)) order by mtl.ocm_shipping_date, mtl.sorc_no

mapper.update:

UPDATE material_partial_detail SET
	belongs=#{belongs}
WHERE
	material_partial_detail_key =#{material_partial_detail_key}
