mapper.select:

    SELECT 
       seq_no,
       image_uuid
    FROM 
       service_repair_analysis_gram	
    WHERE 
        model_name=#{model_name} 
        AND serial_no=#{serial_no} 
        AND rc_mailsend_date=#{rc_mailsend_date}


    SELECT 
      MAX(seq_no)
    FROM 
       service_repair_analysis_gram	
    WHERE 
        model_name=#{model_name} 
        AND serial_no=#{serial_no} 
        AND rc_mailsend_date=#{rc_mailsend_date}

mapper.delete:

	DELETE
	FROM
		service_repair_analysis_gram
	WHERE
		model_name=#{model_name} 
		AND serial_no=#{serial_no} 
		AND rc_mailsend_date=#{rc_mailsend_date} 
		AND seq_no=#{seq_no}

mapper.select:
SELECT mo.name AS model_name, ma.serial_no, coalesce(ma.omr_notifi_no, ma.sorc_no) as sorc_no, ma.reception_time AS reception_time, ma.level, COALESCE(ma.service_repair_flg, 0) as service_repair_flg, ma.ocm, ma.direct_flg, mo.kind FROM material ma LEFT JOIN v_model mo ON ma.model_id=mo.model_id
AND ma.material_id=#{material_id}
AND (ma.sorc_no LIKE CONCAT('%',#{sorc_no},'%') or ma.omr_notifi_no LIKE CONCAT('%',#{sorc_no},'%'))

SELECT *
FROM service_repair_manage
WHERE model_name=#{model_name}
AND serial_no=#{serial_no}
AND rc_mailsend_date=#{rc_mailsend_date}

mapper.update:

	UPDATE
		service_repair_manage
	SET
		material_id=#{new_material_id}
	WHERE
		material_id=#{material_id}

mapper.select:

	SELECT material_id FROM service_repair_manage
	WHERE service_repair_flg = 2
	AND material_id like 'Q%' 
	AND qa_referee_time IS NULL



	AND model_name = #{model_name}



	AND serial_no = #{serial_no};

SELECT ma.reception_time, srm.qa_referee_time, srm.countermeasures, COALESCE(ma.omr_notifi_no,srm.sorc_no, ma.sorc_no) AS sorc_no, srm.service_repair_flg, sra.rc_mailsend_date, sra.model_name, sra.serial_no, sra.analysis_no, sra.analysis_correspond_suggestion, sra.customer_id, sra.customer_name, sra.analysis_result, sra.fix_demand, sra.trouble_discribe, sra.trouble_cause, sra.liability_flg, sra.manufactory_flg, sra.append_component, sra.quantity, sra.loss_amount, sra.last_sorc_no, sra.last_shipping_date, sra.last_rank, sra.last_ocm_rank, sra.last_trouble_feature, sra.wash_feature, sra.disinfect_feature, sra.steriliza_feature, sra.usage_frequency, sra.corresponse_flg, sra.entity_send_flg, sra.trouble_item_reception_date, sra.trouble_item_in_bussiness_date, sra.trouble_item_out_bussiness_date, sra.qis2_date, sra.qis3_date, sra.waste_certificated_date, sra.m_trouble_phenomenon_confirm, sra.m_judgment_result, sra.m_analysis_result_brief, sra.m_correspond_method, sra.m_solutions, sra.setup_date, sra.trouble_happen_date, sra.use_count, sra.use_elapse FROM service_repair_analysis sra left join service_repair_resolve srr ON srr.model_name = sra.model_name and srr.serial_no = sra.serial_no and srr.rc_mailsend_date = sra.rc_mailsend_date join service_repair_manage srm ON srm.model_name = sra.model_name and srm.serial_no = sra.serial_no and srm.rc_mailsend_date = sra.rc_mailsend_date left join material ma ON ma.material_id = srm.material_id
sra.model_name = #{model_name}
AND sra.serial_no = #{serial_no}
AND sra.rc_mailsend_date = #{rc_mailsend_date}
order by rc_mailsend_date desc limit 1

mapper.insert:
REPLACE INTO service_repair_analysis (model_name, serial_no, rc_mailsend_date, analysis_no, customer_name, fix_demand, trouble_cause, trouble_discribe, analysis_result, liability_flg, manufactory_flg, append_component, quantity, loss_amount, last_sorc_no, last_shipping_date, last_rank, last_ocm_rank, last_trouble_feature, wash_feature, disinfect_feature, steriliza_feature, usage_frequency, analysis_correspond_suggestion, m_trouble_phenomenon_confirm, m_judgment_result, m_analysis_result_brief, m_correspond_method, m_solutions, setup_date, trouble_happen_date, use_count, use_elapse
,corresponse_flg
,entity_send_flg ,qis3_date
,trouble_item_reception_date ,trouble_item_in_bussiness_date ,trouble_item_out_bussiness_date ,qis2_date ,waste_certificated_date
) VALUES ( #{model_name}, #{serial_no}, #{rc_mailsend_date}, #{analysis_no}, #{customer_name}, #{fix_demand}, #{trouble_cause}, #{trouble_discribe}, #{analysis_result}, #{liability_flg}, #{manufactory_flg}, #{append_component}, #{quantity}, #{loss_amount}, #{last_sorc_no}, #{last_shipping_date}, #{last_rank}, #{last_ocm_rank}, #{last_trouble_feature}, #{wash_feature}, #{disinfect_feature}, #{steriliza_feature}, #{usage_frequency}, #{analysis_correspond_suggestion}, #{m_trouble_phenomenon_confirm}, #{m_judgment_result}, #{m_analysis_result_brief}, #{m_correspond_method}, #{m_solutions}, #{setup_date}, #{trouble_happen_date}, #{use_count}, #{use_elapse}
,#{corresponse_flg}
,#{entity_send_flg} ,#{qis3_date}
,#{trouble_item_reception_date} ,#{trouble_item_in_bussiness_date} ,#{trouble_item_out_bussiness_date} ,#{qis2_date} ,#{waste_certificated_date}
)

mapper.update:

	UPDATE
		service_repair_manage
	SET
		mention=#{mention}
	WHERE
		model_name=#{model_name}
		and serial_no=#{serial_no}
		and rc_mailsend_date=#{rc_mailsend_date}


	UPDATE
		service_repair_manage
	SET
		QA_RECEPTION_TIME = null
	WHERE
		model_name=#{model_name}
		and serial_no=#{serial_no}
		and rc_mailsend_date=#{rc_mailsend_date}


	UPDATE
		qis_payout
	SET
		rc_mailsend_date=#{rc_mailsend_date}
	WHERE
		model_name=#{model_name} AND serial_no=#{serial_no} AND rc_mailsend_date=#{rc_mailsend_date_org}


	UPDATE
		service_repair_analysis
	SET
		rc_mailsend_date=#{rc_mailsend_date}
	WHERE
		model_name=#{model_name} AND serial_no=#{serial_no} AND rc_mailsend_date=#{rc_mailsend_date_org}
