mapper.select:
SELECT srm.material_id, srm.model_name, srm.serial_no, COALESCE(srm.sorc_no,ma.omr_notifi_no, ma.sorc_no) AS sorc_no, srm.qa_reception_time, srm.rc_mailsend_date, srm.rc_ship_assign_date, cast((f_getworkdays(srm.qa_reception_time, current_timestamp) * 8 + f_diffworktime(srm.qa_reception_time, current_timestamp)) as SIGNED) as answer_in_deadline FROM service_repair_manage srm LEFT JOIN material ma on srm.material_id=ma.material_id WHERE srm.qa_reception_time is NOT null AND srm.qa_referee_time is null AND NOT exists (select 1 from solo_production_feature where serial_no = srm.serial_no and model_name = srm.model_name and judge_date=srm.rc_mailsend_date and action_time is not null and finish_time is null)
AND srm.material_id=#{material_id}
ORDER BY srm.qa_reception_time

mapper.update:

	update 
		qis_payout
	set
		quality_info_no=#{quality_info_no}
	where
		model_name=#{model_name} AND serial_no=	#{serial_no} AND rc_mailsend_date=#{rc_mailsend_date}
