mapper.select:
SELECT a.material_id, coalesce(omr_notifi_no, sorc_no) as sorc_no, a.esas_no, a.model_id, m.name as model_name, m.kind as category_kind, m.category_name, coalesce(a.pat_id, m.default_pat_id) as pat_id, a.serial_no, a.level, a.agreed_date, a.direct_flg, a.fix_type, (select operate_result from production_feature where material_id = a.material_id and position_id = 15 and rework = 0 and operate_result not in (5, 8) order by (1-ISNULL(action_time)), action_time desc limit 1) as img_operate_result , CASE WHEN a.wip_date <= date_add(current_date, interval -2 month) THEN wip_date ELSE NULL END as wip_date, tn.`sap_inline` as inline_time, a.wip_location, a.scheduled_date, a.scheduled_expedited, a.unrepair_flg, a.service_repair_flg, (select finish_time from production_feature where material_id = a.material_id and position_id in (select position_id from position where special_page = 'quotation') and position_id != 101 and operate_result not in (5, 8) order by (1-ISNULL(action_time)), action_time desc limit 1) as quotation_time , exists ( select material_id from production_feature p2 where p2.material_id = a.material_id and p2.position_id = 15 and p2.rework = 0 ) as img_check, exists ( select material_id from production_feature p2 where p2.material_id = a.material_id and p2.position_id = 25 and p2.rework = 0 ) as ccd_change, (select operate_result from production_feature where material_id = a.material_id and position_id = 25 and rework = 0 and operate_result not in (5, 8) order by (1-ISNULL(action_time)), action_time desc limit 1) as ccd_operate_result ,mp.bo_flg as part_order, case when tn.`sap_inline` IS NOT NULL AND level in (9,91,92,93,96,97,98,99) THEN (select group_concat(process_code) from material_process_assign pa join position pos on pa.position_id = pos.position_id where pa.material_id = a.material_id and pa.line_id = 9000000 and prev_position_id = 0) end as process_code from material a JOIN production_feature b on a.material_id = b.material_id and b.position_id in (select position_id from position where special_page = 'quotation') and b.position_id != 101 and b.operate_result = 2 JOIN v_model m on a.model_id = m.model_id LEFT JOIN material_partial mp ON a.material_id = mp.material_id AND mp.occur_times = 1 LEFT JOIN `material_time_node` tn ON a.material_id = tn.material_id
a.break_back_flg = 0 and a.inline_time is null and a.outline_time IS NULL and a.level is not null and a.ocm_rank is not null
and (sorc_no LIKE CONCAT('%', #{sorc_no}, '%') or omr_notifi_no LIKE CONCAT('%', #{sorc_no}, '%'))
and a.model_id = #{model_id}
and a.serial_no = #{serial_no}
and a.level = #{level}
and a.direct_flg = #{direct_flg}
and (a.fix_type = 1 OR (a.fix_type = 2 AND a.outline_time IS NULL))
and a.fix_type = 1
and a.fix_type = 2 AND a.outline_time IS NULL
and a.esas_no = #{esas_no}
and a.agreed_date >= #{agreed_date_start}
and a.agreed_date <= #{agreed_date_end}
and a.wip_location like CONCAT('%','${wip_location}','%')
and a.agreed_date IS NOT NULL
and a.agreed_date IS NULL
order by a.agreed_date IS NULL, part_order in (9, 0, 2) DESC, a.scheduled_expedited DESC, a.direct_flg desc, a.unrepair_flg desc

mapper.update:

UPDATE inline_plan SET 


section_id = #{section_id}


pat_id = #{pat_id}


WHERE material_id = #{material_id};

mapper.select:
SELECT ma.level, ma.agreed_date, ma.fix_type, ma.scheduled_expedited, ma.model_id, ip.pat_id, ma.material_id, ip.section_id, case when ma.quotation_first = 9 then 'true' else null end as ccd_change FROM material ma join inline_plan ip on ma.material_id = ip.material_id where ma.material_id in
#{material_id}
;
