mapper.select:
SELECT `m`.material_id, coalesce(omr_notifi_no, sorc_no) as sorc_no, `m`.esas_no, `m`.model_id, `m`.serial_no, `m`.ocm, `m`.`level`, `m`.direct_flg, `m`.service_repair_flg, `m`.reception_time, `m`.fix_type, `m`.wip_location, `m`.agreed_date, `mdl`.name as model_name, `pf`.operate_result, (current_timestamp - `m`.reception_time > 2000000) * 10 +`m`.quotation_first as quotation_first, `m`.scheduled_expedited + ( CASE WHEN mdl.series = 'URF' THEN exists (select sum(tag_type = 6) xor sum(tag_type = 7) as ts from material_tag where material_id = `m`.material_id group by material_id having ts = 1) ELSE 0 END ) * 4 as scheduled_expedited, mt.tag_type is not null as anml_exp FROM material m JOIN model mdl ON `m`.model_id = `mdl`.model_id JOIN production_feature pf ON `m`.material_id = `pf`.material_id AND `pf`.position_id in
#{position_id}
AND `pf`.operate_result = 0 AND `pf`.action_time is null LEFT JOIN `material_tag` mt ON `m`.material_id = mt.material_id AND tag_type = 1 ORDER BY mdl.series = 'URF' desc, operate_result DESC, reception_time

SELECT `m`.material_id, coalesce(omr_notifi_no, sorc_no) as sorc_no, `m`.esas_no, `m`.model_id, `m`.serial_no, `m`.ocm, COALESCE(`m`.wip_location, '同意') as wip_location, `m`.`level`, `m`.direct_flg, `m`.service_repair_flg, `m`.reception_time, `m`.fix_type, (CASE WHEN `m`.level = 9 OR `m`.level = 91 OR `m`.level = 92 OR `m`.level = 93 OR `m`.level = 96 OR `m`.level = 97 OR `m`.level = 98 THEN 8 WHEN mdl.category_id = 00000000013 OR `m`.fix_type = 2 THEN 4 WHEN mdl.kind = 06 THEN 5 WHEN mdl.kind = 03 THEN 6 WHEN mdl.kind = 07 THEN 5 WHEN `m`.level = 1 THEN 8 ELSE 15 END + (coalesce(`m`.service_repair_flg, 0) = 2)) as ticket_flg, `m`.wip_location, `m`.agreed_date, `mdl`.name as model_name, `m`.inline_time, pf.finish_time as quotation_time FROM material m JOIN v_model mdl ON `m`.model_id = `mdl`.model_id JOIN production_feature pf ON `m`.material_id = `pf`.material_id AND `pf`.position_id in
#{position_id}
AND `pf`.operate_result = 2 AND `m`.break_back_flg = 0 AND `m`.inline_time IS NULL AND `m`.outline_time IS NULL AND pf.finish_time > current_date ORDER BY quotation_time DESC
