mapper.select:
SELECT sw.partial_id, sw.process_type, sw.process_time, CASE WHEN sw.process_type = 5 THEN cstor.case_code ELSE sw.lot_no END as lot_no, sw.quantity, partial.code, operator.name AS operator_name, sw.material_id, material.omr_notifi_no AS sorc_no, cpat.goods_id AS operator_id, material.scheduled_date AS process_time_end FROM steel_wire_container_wash_process sw JOIN partial ON sw.partial_id = partial.partial_id LEFT JOIN operator ON sw.operator_id = operator.operator_id LEFT JOIN material ON sw.material_id = material.material_id LEFT JOIN compose_storage cstor ON sw.material_id = cstor.goods_id AND sw.process_type = 5 AND cstor.line_id = 00000000013 LEFT JOIN compose_storage cpat ON sw.material_id = cpat.goods_id AND sw.process_type = 5 AND cstor.line_id = 00000000012
AND sw.process_type = #{process_type}
AND partial.code LIKE CONCAT(#{code}, '%')
AND sw.lot_no = #{lot_no}
AND (sw.process_time >= #{process_time_start} OR sw.process_time < '2012-10-1')
AND sw.process_time BETWEEN '2012-10-1' AND DATE_ADD(#{process_time_end}, interval 1 day)
AND sw.operator_id = #{operator_id}
ORDER BY sw.process_time ASC

mapper.update:

UPDATE 
	steel_wire_container_wash_process 
SET 
	quantity = #{quantity}



	,lot_no = #{lot_no}



WHERE
	partial_id = #{partial_id}
AND
	process_type = #{process_type}
AND
	process_time = #{process_time}


UPDATE 
	steel_wire_container_wash_process 
SET 
	material_id = #{material_id}
WHERE
	partial_id = #{partial_id}
AND
	process_type = #{process_type}
AND
	process_time = #{process_time}
