mapper.select:
SELECT sw.partial_id, sw.process_type, sw.process_time, sw.lot_no, sw.quantity, partial.code, operator.name AS operator_name, COALESCE(material.omr_notifi_no, material.sorc_no) AS sorc_no FROM steel_wire_container_wash_process sw JOIN partial ON sw.partial_id = partial.partial_id JOIN operator ON sw.operator_id = operator.operator_id LEFT JOIN material ON sw.material_id = material.material_id
AND sw.process_type = #{process_type}
AND partial.code LIKE CONCAT(#{code}, '%')
AND sw.lot_no = #{lot_no}
AND sw.process_time >= #{process_time_start}
AND sw.process_time < DATE_ADD(#{process_time_end}, interval 1 day)
AND sw.operator_id = #{operator_id}
ORDER BY sw.process_time ASC

mapper.update:

UPDATE 
	steel_wire_container_wash_process 
SET 
	quantity = #{quantity}



	,lot_no = #{lot_no}



WHERE
	partial_id = #{partial_id}
AND
	process_type = #{process_type}
AND
	process_time = #{process_time}


UPDATE 
	steel_wire_container_wash_process 
SET 
	material_id = #{material_id}
WHERE
	partial_id = #{partial_id}
AND
	process_type = #{process_type}
AND
	process_time = #{process_time}
