mapper.select:
SELECT SUM(wtr.wait_partial_distrubute_time) AS wait_partial_distrubute_time, SUM(wtr.desc_work_time) AS desc_work_time, SUM(wtr.desc_wait_time) AS desc_wait_time, SUM(wtr.desc_drying_time) AS desc_drying_time, SUM(wtr.ns_work_time) AS ns_work_time, SUM(wtr.ns_wait_time) AS ns_wait_time, SUM(wtr.ns_drying_time) AS ns_drying_time, SUM(wtr.com_work_time) AS com_work_time, SUM(wtr.com_wait_time) AS com_wait_time, SUM(wtr.com_drying_time) AS com_drying_time, SUM(wtr.wait_bo_partial_time) AS wait_bo_partial_time, SUM(wtr.exception_break_time) AS exception_break_time, SUM(wtr.total_work_time) AS total_work_time, COUNT(*) AS number, ma.level FROM waitting_time_report wtr JOIN material ma ON wtr.material_id = ma.material_id WHERE wtr.material_id IN
#{material_id}
GROUP BY ma.level
