mapper.select:
SELECT sd.supplies_key, sd.product_name, sd.model_name, sd.unit_price, sd.unit_text, sd.quantity, sd.section_id, section.name AS section_name, sd.applicator_id, applicator_op.name AS applicator_name, sd.nesssary_reason, sd.applicate_date, sd.confirmer_id, confirmer_op.name AS confirmer_name, sd.order_key, so.order_no, sd.supplier, sd.scheduled_date, sd.recept_date, sd.budget_month, sd.inline_recept_date, sd.comments, sd.invoice_no FROM supplies_detail sd JOIN section ON sd.section_id = section.section_id JOIN operator applicator_op ON applicator_op.operator_id = sd.applicator_id LEFT JOIN operator confirmer_op ON confirmer_op.operator_id = sd.confirmer_id LEFT JOIN supplies_order so ON so.order_key = sd.order_key
AND sd.product_name LIKE CONCAT('%', #{product_name}, '%')
AND sd.model_name LIKE CONCAT('%', #{model_name}, '%')
AND sd.section_id = #{section_id}
AND sd.applicator_id = #{applicator_id}
AND sd.applicate_date >= #{applicate_date_start}
AND sd.applicate_date <= #{applicate_date_end}
AND sd.confirmer_id = #{confirmer_id}
AND so.order_no = #{order_no}
AND sd.scheduled_date >= #{scheduled_date_start}
AND sd.scheduled_date <= #{scheduled_date_end}
AND sd.recept_date >= #{recept_date_start}
AND sd.recept_date <= #{recept_date_end}
AND sd.budget_month = #{budget_month}
AND sd.inline_recept_date >= #{inline_recept_date_start}
AND sd.inline_recept_date <= #{inline_recept_date_end}
AND sd.invoice_no = #{invoice_no}
ORDER BY sd.applicate_date DESC

mapper.update:

UPDATE
	supplies_detail
SET
	confirmer_id = #{confirmer_id}



	,order_key = #{order_key}



WHERE
	supplies_key = #{supplies_key}


UPDATE
	supplies_detail
SET
	order_key = #{order_key}
WHERE
	supplies_key = #{supplies_key}


UPDATE
	supplies_detail
SET
	recept_date = #{recept_date}
WHERE
	supplies_key = #{supplies_key}


UPDATE
	supplies_detail
SET
	inline_recept_date = #{inline_recept_date}
WHERE
	supplies_key = #{supplies_key}


UPDATE
	supplies_detail
SET
	invoice_no = #{invoice_no}
WHERE
	supplies_key = #{supplies_key}
